var express=require("express"),router=express.Router(),mongojs=require("mongojs"),db=mongojs("tweets"),tweets=db.collection("tweets"),bodyParser=require("body-parser");router.use(bodyParser.json()),router.route("/tweets").get(function(e,t){console.log("======>GET Request of Tweets<======");var o={createdBy:e.query.id};db.tweets.find(o).toArray(function(e,o){t.json(o)})}).post(function(e,t){console.log("======>POST Request of Tweets<======"),db.tweets.insert(e.body,function(e,o){t.json(o)})}),router.delete("/tweets/:id",function(e,t){console.log("======>DELETE Request of Tweets<======");var o=e.params.id;db.tweets.remove({_id:mongojs.ObjectId(o)},function(e,o){t.json(o)})}),module.exports=router;